<?php
/*
Plugin Name: OpenSwimData Icons
Author Name: Patrick Hesselberg
Description: Adds OpenSwimData icons
Version: 1.0
*/

class OSD_Icons {
	static public $PLUGIN_URL;
	static public $PLUGIN_DIR;

	function __construct() {
		$this->vars();
		$this->icons();
	}

	function vars() {
		self::$PLUGIN_URL = plugins_url( '/', __FILE__ );
		self::$PLUGIN_DIR = plugin_dir_path( __FILE__ );
	}

	function icons() {
		$this->hooks();
	}

	function hooks() {
		add_action( 'admin_head', array( &$this, 'admin_logo' ) );
		add_action( 'wp_head', array( &$this, 'admin_logo' ) );
		add_action( 'wp_head', array( &$this, 'favicons' ) );
		add_action( 'login_head', array( &$this, 'logo_login' ) );
	}

	function admin_logo() {
		// Make user not logged-in users will never get further.
		if ( !is_user_logged_in() )
			return false;

		$d = "url(data:image/svg+xml;base64,)";
		$output = '#wpadminbar #wp-admin-bar-wp-logo > .ab-item .ab-icon:before{content:"";}';
		$output .= "#wpadminbar #wp-admin-bar-wp-logo > .ab-item .ab-icon{background-image: $d !important; background-repeat: no-repeat; background-position: 0 6px !important; background-size: 20px 20px;}";

		echo '<style type="text/css">' . $output . '</style>' . PHP_EOL;
	}

	function logo_login() {
		$d = 'url(data:image/svg+xml;base64,)';
		$output = "h1 a { background-image: $d !important; }";
		echo '<style type="text/css">' . $output . '</style>' . PHP_EOL;
	}

	function favicons() {
		?>
		<link rel="apple-touch-icon" sizes="57x57" href="<?php echo $this->get_icon_url( 'apple-touch-icon-57x57.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="114x114" href="<?php echo $this->get_icon_url( 'apple-touch-icon-114x114.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="72x72" href="<?php echo $this->get_icon_url( 'apple-touch-icon-72x72.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="144x144" href="<?php echo $this->get_icon_url( 'apple-touch-icon-144x144.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="60x60" href="<?php echo $this->get_icon_url( 'apple-touch-icon-60x60.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="120x120" href="<?php echo $this->get_icon_url( 'apple-touch-icon-120x120.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="76x76" href="<?php echo $this->get_icon_url( 'apple-touch-icon-76x76.png' ); ?>" />
		<link rel="apple-touch-icon" sizes="152x152" href="<?php echo $this->get_icon_url( 'apple-touch-icon-152x152.png' ); ?>" />
		<link rel="shortcut icon" href="<?php echo $this->get_icon_url( '' ); ?>favicon.ico" />
		<link rel="icon" type="image/png" href="<?php echo $this->get_icon_url( 'favicon-16x16.png' ); ?>" sizes="16x16" />
		<link rel="icon" type="image/png" href="<?php echo $this->get_icon_url( 'favicon-32x32.png' ); ?>" sizes="32x32" />
		<link rel="icon" type="image/png" href="<?php echo $this->get_icon_url( 'favicon-96x96.png' ); ?>" sizes="96x96" />
		<link rel="icon" type="image/png" href="<?php echo $this->get_icon_url( 'favicon-160x160.png' ); ?>" sizes="160x160" />
		<meta name="msapplication-TileColor" content="#2b5797" />
		<meta name="msapplication-TileImage" content="<?php echo $this->get_icon_url( 'mstile-144x144.png' ); ?>" />
		<meta name="msapplication-square70x70logo" content="<?php echo $this->get_icon_url( 'mstile-70x70.png' ); ?>" />
		<meta name="msapplication-square150x150logo" content="<?php echo $this->get_icon_url( 'mstile-150x150.png' ); ?>" />
		<meta name="msapplication-wide310x150logo" content="<?php echo $this->get_icon_url( 'mstile-310x150.png' ); ?>" />
		<meta name="msapplication-square310x310logo" content="<?php echo $this->get_icon_url( 'mstile-310x310.png' ); ?>" />
		<?php
	}

	function get_assets_dir() {
		return self::$PLUGIN_DIR . 'assets/';
	}

	function get_assets_url() {
		return self::$PLUGIN_URL . 'assets/';
	}

	function get_icon_url( $file ) {
		return $this->get_assets_url() . 'icons/' . $file;
	}
}

new OSD_Icons();

